import React, { Component } from 'react';
import { render } from 'react-dom';

function Temporario(){

    state = {
        name: '',
        nameError: ''
    }

    onChange = (e) => {
        this.setState({ [e.target.name]: e.target.value, [`${e.target.name}Error`]:  '' })

    }

    onSubmit = (e) => {
        e.preventDefault();
        this.checkName();
    }

    checkName = () => {
        const { name } = this.state;

        if (name.length < 5) {
        this.setState({
            nameError: 'Please enter more characters.'
        })}
    }

    // renderError(){

    //     const { nameError } = this.state;

    //     if (nameError) {
    //     return <div>{nameError}</div>
    //     };

    //     return null;
    // }

        return (
        <div className="register">
            <div className="container">
            <div className="row">
                <div className="col-md-8 m-auto">
                <h1 className="display-4 text-center">Sign Up</h1>
                <p className="lead text-center">Create your account</p>
                <form className="container" onSubmit={this.onSubmit} noValidate >
                    <div className="form-group">
                    <label className="form-control-label" htmlFor="inputSuccess1">Your Name:</label>
                    <div className="col-xs-5 selectContainer">
                        <input id="inputSuccess1" type="text" className="form-control form-control-lg" placeholder="Name" name="name" value={this.state.name} onChange={this.onChange}/>
                        {/* {this.renderError()} */}
                    </div>
                    </div>

                    <input type="submit" className="btn btn-info btn-block mt-4" />
                </form>
                </div>
            </div>
            </div>
        </div>
        )
}

export default Temporario;
